 import React, { useState, useReducer, useEffect } from "react";
import clsx from 'clsx';
import { createStyles, lighten, makeStyles, Theme } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import DeleteIcon from '@material-ui/icons/Delete';

 import axios from "axios";
 import reducer from "../../actions/promotionReducer";

import { MenuItem, Select } from "@material-ui/core";
import {
  addCalculation,
  deleteCalculationById,
  updateCalculation,
  getCalculationById,
} from "../../actions/promotionApiActions";
//import "./promotionTable.css";

 const URL = "https://localhost:44306"; 

 const buildQueryString = (skip: any, take: any) =>
   `${URL}/GetCalcList`;

const initialState = {
  rows: [],
  loading: false
};

 export default function CalculationList() {
   const [state, dispatch] = useReducer(reducer, initialState);

  // const getRemoteRows = (requestedSkip: number, take: number) => {
  //   dispatch({ type: "START_LOADING", payload: { requestedSkip, take } });
