//   const handleCalculationDelete = (calculationId: string) => {
//     deleteCalculationById(calculationId)
//       .then(() => {
//         setSelection([]);
//         const updatedRows = rows.filter(function (row: any) {
//           return row._id !== calculationId;
//         });
//         dispatch({
//           type: "UPDATE_ROWS",
//           payload: {
//             rows: updatedRows,
//           },
//         });
//       })
//       .catch(() => dispatch({ type: "REQUEST_ERROR" }));
//   };

//   // const handlePromotionDuplicate = (promotionId: string) => {
//   //   let duplicatePromotion = _.find(rows, { _id: promotionId });
//   //   addCalc(duplicatePromotion)
//   //     .then((response) => {
//   //       setSelection([]);
//   //       const newPromotion = response.data.promotion;
//   //       const updatedRows = [...rows];
//   //       updatedRows.unshift(newPromotion);
//   //       dispatch({
//   //         type: "UPDATE_ROWS",
//   //         payload: {
//   //           rows: updatedRows,
//   //         },
//   //       });
//   //     }) 
//   //     .catch(() => dispatch({ type: "REQUEST_ERROR" }));
//   // };

useEffect(() => loadData());
const { rows } = state;

const loadData = () => {
  const { requestedSkip, take, lastQuery, loading } = state;
  const query = buildQueryString(requestedSkip, take);
  if (query !== lastQuery && !loading) {
    dispatch({ type: "FETCH_INIT" });
    axios
      .get(query)
      .then((response) => {          
        dispatch({
          type: "UPDATE_ROWS",
          payload: {
            rows: response.data,//.promotions,
          },
        });
      })
      .catch(() => dispatch({ type: "REQUEST_ERROR" }));
    dispatch({ type: "UPDATE_QUERY", payload: query });
  }
};

  
  
//   const columns = [    
//    {
//       field: 'calculation',
//       headerName: 'Calculations',
//       sortable: false,
//       width: 160,
//       valueGetter: (params: any) =>
//       `${params.row.num1} ${params.row.oper} ${params.row.num2} = ${params.row.result}`
