import React from "react";
import { FormControl, InputLabel, Select, MenuItem } from "@material-ui/core";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

import "./promotionEditModal.css";

export interface PromotionEditModalProps {
  promotion: IPromotion;
  handleCloseEditModal: () => void;
  handleUpdatePromotion: (promotion: IPromotion) => void;
  open: boolean;
}

export interface PromotionEditModalState {}

export default function PromotionEditModal(props: PromotionEditModalProps) {
  const { promotion } = props;
  const [newPromotion, setState] = React.useState({
    ...promotion,
  });
  const startDateValue = new Date(promotion.promotionStartDate)
    .toISOString()
    .split("T")[0];

  const endDateValue = new Date(promotion.promotionEndDate)
    .toISOString()
    .split("T")[0];

  const handleChange = (event: any) => {
    const { name, value } = event.target;
    setState((prevState: any) => {
      return {
        ...prevState,
        [name]: value,
      };
    });
  };

  return (
    <Dialog
      open={props.open}
      onClose={props.handleCloseEditModal}
      aria-labelledby="alert-dialog-title"
      aria-describedby="alert-dialog-description"
    >
      <DialogTitle id="alert-dialog-title">Update Promotion</DialogTitle>
      <DialogContent>
        <DialogContentText id="alert-dialog-description">
          <form className="updatePromotionForm" noValidate autoComplete="off">
            <TextField
              id="promotionName"
              name="promotionName"
              label="Promotion Name"
              defaultValue={newPromotion.promotionName}
              onChange={handleChange}
            />
            <FormControl style={{ width: "140px" }}>
              <InputLabel id="promotionType">Promotion Type</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                name="promotionType"
                value={newPromotion.promotionType}
                onChange={handleChange}
              >
                <MenuItem value={"Basic"}>Basic</MenuItem>
                <MenuItem value={"Common"}>Common</MenuItem>
                <MenuItem value={"Epic"}>Epic</MenuItem>
              </Select>
            </FormControl>
            <TextField
              id="startDate"
              name="promotionStartDate"
              label="Start Date"
              type="date"
              defaultValue={startDateValue}
              InputLabelProps={{
                shrink: true,
              }}
              onChange={handleChange}
            />
            <TextField
              id="endDate"
              name="promotionEndDate"
              label="End Date"
              type="date"
              defaultValue={endDateValue}
              InputLabelProps={{
                shrink: true,
              }}
              onChange={handleChange}
            />
            <TextField
              id="userGroupName"
              name="userGroupName"
              label="User Group Name"
              defaultValue={newPromotion.userGroupName}
              onChange={handleChange}
            />
          </form>
        </DialogContentText>
      </DialogContent>
      <DialogActions>
        <Button onClick={props.handleCloseEditModal} color="primary">
          cancel
        </Button>
        <Button
          onClick={() => props.handleUpdatePromotion(newPromotion)}
          color="primary"
          autoFocus
        >
          save
        </Button>
      </DialogActions>
    </Dialog>
  );
}
