import React, { useReducer } from "react";
import { FormControl, InputLabel, Select, MenuItem } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";

import reducer from "../../actions/calculatorReducer";

import {
    addCalculation,
    deleteCalculationById,
    updateCalculation,
    getCalculationById,
} from "../../actions/calculatorApiActions";

export interface CalculatorProps {
    editCalculation?: ICalculation
}

export interface CalculatorState { }

export function Calculator(props: CalculatorProps) {
    const initialState = {
        // calculation: {} as ICalculation
    }
    const { editCalculation } = props;
    const [newCalculation, setState] = React.useState({
        ...editCalculation,
    });
    const [state, dispatch] = useReducer(reducer, initialState);

    const handleChange = (event: any) => {
        const { name, value } = event.target;
        setState((prevState: any) => {
            return {
                ...prevState,
                [name]: value,
            };
        });
    };

    return (
        <form className="updatePromotionForm" noValidate autoComplete="off">
            <TextField
                id="num1"
                name="num1"
                defaultValue={newCalculation.num1}
                onChange={handleChange}
            />
            <FormControl style={{ width: "140px" }}>
                <Select
                    id="demo-simple-select"
                    name="oper"
                    value={newCalculation.oper}
                    onChange={handleChange}
                >
                    <MenuItem value={"+"}>+</MenuItem>
                    <MenuItem value={"-"}>-</MenuItem>
                    <MenuItem value={"*"}>*</MenuItem>
                    <MenuItem value={":"}>:</MenuItem>
                </Select>
            </FormControl>
            <TextField
                id="num2"
                name="num2"
                defaultValue={newCalculation.num2}
                onChange={handleChange}
            />
        </form>
    );
}
export default Calculator;
