import React, { useState, useReducer, useEffect } from "react";
import Paper from "@material-ui/core/Paper";
import {
  Column,
  DataTypeProvider,
  RowDetailState,
  SelectionState,
  VirtualTableState,
} from "@devexpress/dx-react-grid";
import {
  Grid,
  VirtualTable,
  TableHeaderRow,
  TableSelection,
} from "@devexpress/dx-react-grid-material-ui";
import axios from "axios";
import reducer from "../../actions/promotionReducer";
import {
  columnExtensions,
  promotionsTableColumns,
} from "../../common/columnSchema";
import { MenuItem, Select } from "@material-ui/core";
import {
 // addCalculation,
  deleteCalculationById,
  updateCalculation,
  getCalculationById,
} from "../../actions/promotionApiActions";
import * as _ from "lodash";
import PromotionEditModal from "../promotionEditModal/promotionEditModal";
import "./promotionTable.css";

const VIRTUAL_PAGE_SIZE = 200;
const MAX_ROWS = 10000;
const URL = "https://localhost:44306";

const getRowId = (row: any) => row._id;
const buildQueryString = (skip: any, take: any) =>
  `${URL}/GetCalcList`;

const initialState = {
  rows: [],
  skip: 0,
  requestedSkip: 0,
  take: VIRTUAL_PAGE_SIZE * 2,
  totalCount: 0,
  loading: false,
  lastQuery: "",
};

const DateFormatter = ({ value }: { value: any }) => value.split("T")[0];

const DateTypeProvider = (props: any) => (
  <DataTypeProvider formatterComponent={DateFormatter} {...props} />
);

export default function PromotionTable() {
  const [state, dispatch] = useReducer(reducer, initialState);
  const columns: Column[] = promotionsTableColumns;
  const [tableColumnExtensions] = useState(columnExtensions);
  const [selection, setSelection] = useState<any | null>([]);
  const [dateColumns] = useState(["promotionStartDate", "promotionEndDate"]);
  const [openEditModal, setOpenEditModal] = useState(false);
  const [editPromotion, setEditCalculation] = useState<any | null>();

  const getRemoteRows = (requestedSkip: number, take: number) => {
    dispatch({ type: "START_LOADING", payload: { requestedSkip, take } });
  };

  const handleCloseEditModal = () => {
    setOpenEditModal(false);
    setEditCalculation(null);
    setSelection([]);
  };

  const handleCalculationEdit = (calculationId: string) => {
    getCalculationById(calculationId).then((response) => {
      let editCalculation: ICalculation | undefined = response.data.calculation;
      setEditCalculation(editCalculation);
      setOpenEditModal(true);
    });
  };

  const handleUpdatePromotion = (updateCalc: ICalculation) => {
    updateCalculation(updateCalc).then((response) => {
      let updatedRows = rows.map((row: any) =>
        row.id === updateCalc.id ? updateCalc : row
      );
      dispatch({
        type: "UPDATE_ROWS",
        payload: {
          rows: updatedRows,
        },
      });
      setSelection([]);
      setOpenEditModal(false);
      setEditCalculation(null);
    });
  };

  const handleCalculationDelete = (calculationId: string) => {
    deleteCalculationById(calculationId)
      .then(() => {
        setSelection([]);
        const updatedRows = rows.filter(function (row: any) {
          return row._id !== calculationId;
        });
        dispatch({
          type: "UPDATE_ROWS",
          payload: {
            rows: updatedRows,
          },
        });
      })
      .catch(() => dispatch({ type: "REQUEST_ERROR" }));
  };

  // const handlePromotionDuplicate = (promotionId: string) => {
  //   let duplicatePromotion = _.find(rows, { _id: promotionId });
  //   addCalc(duplicatePromotion)
  //     .then((response) => {
  //       setSelection([]);
  //       const newPromotion = response.data.promotion;
  //       const updatedRows = [...rows];
  //       updatedRows.unshift(newPromotion);
  //       dispatch({
  //         type: "UPDATE_ROWS",
  //         payload: {
  //           rows: updatedRows,
  //         },
  //       });
  //     }) 
  //     .catch(() => dispatch({ type: "REQUEST_ERROR" }));
  // };

  const SelectActionType = () => (
    <Select
      style={{ width: "100%" }}
      disabled={selection.length !== 1}
      defaultValue=""
    >
      <MenuItem
        value="edit"
        key={1}
        onClick={() => handleCalculationEdit(selection[0])}
      >
        Edit
      </MenuItem>
      <MenuItem
        value="delete"
        key={2}
        onClick={() => handleCalculationDelete(selection[0])}
      >
        Delete
      </MenuItem>
      {/* <MenuItem
        key={3}
        value="duplicate"
        onClick={() => handlePromotionDuplicate(selection[0])}
      >
        Duplicate
      </MenuItem> */}
    </Select>
  );

  const SelectTypeProvider = (props: any) => {
    return (
      <DataTypeProvider
        disabled={selection.length !== 1}
        formatterComponent={SelectActionType}
        {...props}
      />
    );
  };

  const loadData = () => {
    const { requestedSkip, take, lastQuery, loading } = state;
    const query = buildQueryString(requestedSkip, take);
    if (query !== lastQuery && !loading) {
      dispatch({ type: "FETCH_INIT" });
      axios
        .get(query)
        .then((response) => {          
          dispatch({
            type: "UPDATE_ROWS",
            payload: {
              skip: requestedSkip,
              rows: response,//.data.calcList,
              totalCount: MAX_ROWS,
            },
          });
        })
        .catch(() => dispatch({ type: "REQUEST_ERROR" }));
      dispatch({ type: "UPDATE_QUERY", payload: query });
    }
  };

  useEffect(() => loadData());
  const { rows, skip, totalCount, loading } = state;

  return (
    <Paper className="promotionTable">
      <h1>Our Promotions</h1>
      <Grid rows={rows} columns={columns} getRowId={getRowId}>
        <SelectionState
          selection={selection}
          onSelectionChange={setSelection}
        />
        <SelectTypeProvider
          for={["Action"]}
          rowComponent
          row={RowDetailState}
        />
        <DateTypeProvider for={dateColumns} />

        <VirtualTableState
          infiniteScrolling
          loading={loading}
          totalRowCount={totalCount}
          pageSize={VIRTUAL_PAGE_SIZE}
          skip={skip}
          getRows={getRemoteRows}
        />
        <VirtualTable columnExtensions={tableColumnExtensions} />
        <TableSelection highlightRow={true} />
        <TableHeaderRow />
        {editPromotion && (
          <PromotionEditModal
            calculation={editPromotion}
            handleCloseEditModal={handleCloseEditModal}
            open={openEditModal}
            handleUpdateCalculation={handleUpdatePromotion}
          />
        )}
      </Grid>
    </Paper>
  );
}
