import React from "react";
import { FormControl, InputLabel, Select, MenuItem } from "@material-ui/core";
import NumberFormat from 'react-number-format';

import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";

import './calculator.css'

import {
    saveCalculation
} from "../../actions/calculatorApiActions";

export interface CalculatorProps {
    newCalculation: ICalculation,
    setState: any,
    onCalculationSave: (event: React.MouseEvent<unknown>, updatedRows: ICalculation[]) => void
}

export interface CalculatorState { }


interface NumberFormatCustomProps {
    inputRef: (instance: NumberFormat | null) => void;
    onChange: (event: { target: { name: string; value: string } }) => void;
    name: string;
  }
  
  function NumberFormatCustom(props: NumberFormatCustomProps) {
    const { inputRef, onChange, ...other } = props;
  
    return (
      <NumberFormat
        {...other}
        getInputRef={inputRef}
        onValueChange={(values: any) => {
          onChange({
            target: {
              name: props.name,
              value: values.value,
            },
          });
        }}
        thousandSeparator
        isNumericString
        prefix="$"
      />
    );
  }
const Calculator = (props: CalculatorProps) => {
    const { newCalculation, setState } = props;

    const handleChange = (event: any) => {
        const { name, value } = event.target;
        setState((prevState: any) => {
            return {
                ...prevState,
                [name]: value,
            };
        });
    };

    const handleSaveCalculation = (event: any) => {
        saveCalculation(newCalculation)
            .then((response: any) => {
                const updatedRows: any[] = response.data;
                setState((prevState: any) => {
                    return {
                        ...prevState,
                        id: (updatedRows[0] as any).id,
                        result: (updatedRows[0] as any).result
                    };
                });
                props.onCalculationSave(event, updatedRows as ICalculation[]);
            });
    }

    return (
        <form className="updatePromotionForm" noValidate autoComplete="off">
            <TextField
                id="num1"
                name="num1"
                required
                InputProps={{
                    inputComponent: NumberFormatCustom as any,
                  }}
                value={newCalculation.num1 || ''}
                onChange={handleChange}
            />
            <FormControl style={{ width: "140px" }}>
                <Select
                    id="demo-simple-select"
                    name="oper"
                    value={newCalculation.oper || ''}
                    onChange={handleChange}
                >
                    <MenuItem value={1}>+</MenuItem>
                    <MenuItem value={2}>-</MenuItem>
                    <MenuItem value={3}>*</MenuItem>
                    <MenuItem value={4}>:</MenuItem>
                </Select>
            </FormControl>
            <TextField
                id="num2"
                name="num2"
                value={newCalculation.num2 || ''}
                onChange={handleChange}
            />
            <Button
                onClick={() => handleSaveCalculation(newCalculation)}
                color="primary"
                autoFocus
            >=</Button>
            <InputLabel id="result">{newCalculation.result || ''}</InputLabel>            
        </form>        
    );
}

export default Calculator;
