import axios, { AxiosResponse } from "axios";

const baseUrl: string = "http://localhost:4000";

export const getPromotions = async (
  skip: number,
  take: number
): Promise<AxiosResponse<ApiDataType>> => {
  try {
    const promotions: AxiosResponse<ApiDataType> = await axios.get(
      baseUrl + `/getAllPromotions?skip=${skip}&take=${take}`
    );
    return promotions;
  } catch (error) {
    throw new Error(error);
  }
};

export const getPromotionById = async (
  _id: string
): Promise<AxiosResponse<ApiDataType>> => {
  try {
    const promotion: AxiosResponse<ApiDataType> = await axios.get(
      baseUrl + `/getPromotionById/${_id}`
    );
    return promotion;
  } catch (error) {
    throw new Error(error);
  }
};

export const addPromotion = async (
  newPromotion: IPromotion
): Promise<AxiosResponse<ApiDataType>> => {
  try {
    const promotion: Omit<IPromotion, "_id"> = {
      promotionName: newPromotion.promotionName,
      promotionType: newPromotion.promotionType,
      promotionStartDate: newPromotion.promotionStartDate,
      promotionEndDate: newPromotion.promotionEndDate,
      userGroupName: newPromotion.userGroupName,
    };
    const savePromotion: AxiosResponse<ApiDataType> = await axios.post(
      baseUrl + "/addPromotion",
      promotion
    );
    return savePromotion;
  } catch (error) {
    throw new Error(error);
  }
};

export const updatePromotion = async (
  promotion: IPromotion
): Promise<AxiosResponse<ApiDataType>> => {
  try {
    const promotionUpdate: Pick<IPromotion, any> = {
      promotionName: promotion.promotionName,
      promotionType: promotion.promotionType,
      promotionStartDate: promotion.promotionStartDate,
      promotionEndDate: promotion.promotionEndDate,
      userGroupName: promotion.userGroupName,
    };
    const updatedPromotion: AxiosResponse<ApiDataType> = await axios.put(
      `${baseUrl}/updatePromotion/${promotion._id}`,
      promotionUpdate
    );
    return updatedPromotion;
  } catch (error) {
    throw new Error(error);
  }
};

export const deletePromotionById = async (
  _id: string
): Promise<AxiosResponse<ApiDataType>> => {
  try {
    const deletedPromotion: AxiosResponse<ApiDataType> = await axios.delete(
      `${baseUrl}/deletePromotion/${_id}`
    );
    return deletedPromotion;
  } catch (error) {
    throw new Error(error);
  }
};

export const deleteAllPromotions = async (): Promise<
  AxiosResponse<ApiDataType>
> => {
  try {
    const deletedPromotion: AxiosResponse<ApiDataType> = await axios.delete(
      `${baseUrl}/deleteAllPromotions`
    );
    return deletedPromotion;
  } catch (error) {
    throw new Error(error);
  }
};
