import React, { useState, useReducer, useEffect } from "react";
import Paper from "@material-ui/core/Paper";
import { DataTypeProvider, VirtualTableState } from "@devexpress/dx-react-grid";
import {
  Grid,
  VirtualTable,
  TableHeaderRow,
} from "@devexpress/dx-react-grid-material-ui";
import axios, { AxiosResponse } from "axios";
import reducer from "../actions/promotionReducer";

const VIRTUAL_PAGE_SIZE = 100;
const MAX_ROWS = 10000;
const URL = "http://localhost:4000/getAllPromotions";
const getRowId = (row: any) => row._id;
const buildQueryString = (skip: any, take: any) =>
  `${URL}?skip=${skip}&take=${take}`;

const initialState = {
  rows: [],
  skip: 0,
  requestedSkip: 0,
  take: VIRTUAL_PAGE_SIZE * 2,
  totalCount: 0,
  loading: false,
  lastQuery: "",
};

const DateFormatter = ({ value }: { value: any }) => value.split("T")[0];

const DateTypeProvider = (props: any) => (
  <DataTypeProvider formatterComponent={DateFormatter} {...props} />
);

export default function PromotionTable() {
  const [state, dispatch] = useReducer(reducer, initialState);

  const columns = [
    {
      name: "promotionName",
      title: "Name",
      getCellValue: (row: any) => row.promotionName,
    },
    {
      name: "promotionType",
      title: "Type",
      getCellValue: (row: any) => row.promotionType,
    },
    {
      name: "promotionStartDate",
      title: "Start Date",
      getCellValue: (row: any) => row.promotionStartDate,
    },
    {
      name: "promotionEndDate",
      title: "End Date",
      getCellValue: (row: any) => row.promotionEndDate,
    },
    {
      name: "userGroupName",
      title: "User Group Name",
      getCellValue: (row: any) => row.userGroupName,
    },
    { name: "Action", title: "Action" },
  ];
  const [tableColumnExtensions] = useState([
    { columnName: "promotionName", width: 220 },
    { columnName: "promotionType", width: 220 },
    { columnName: "promotionStartDate", width: 220 },
    { columnName: "promotionEndDate", width: 220 },
    { columnName: "userGroupName", width: 220 },
    { columnName: "Action", width: 120 },
  ]);
  const [dateColumns] = useState(["promotionStartDate", "promotionEndDate"]);
  const getRemoteRows = (requestedSkip: any, take: any) => {
    dispatch({ type: "START_LOADING", payload: { requestedSkip, take } });
  };

  const loadData = () => {
    const { requestedSkip, take, lastQuery, loading } = state;
    const query = buildQueryString(requestedSkip, take);
    if (query !== lastQuery && !loading) {
      dispatch({ type: "FETCH_INIT" });
      axios
        .get(query)
        .then((response) => {
          console.log(response.data.promotions);
          dispatch({
            type: "UPDATE_ROWS",
            payload: {
              skip: requestedSkip,
              rows: response.data.promotions,
              totalCount: MAX_ROWS,
            },
          });
        })
        .catch(() => dispatch({ type: "REQUEST_ERROR" }));
      dispatch({ type: "UPDATE_QUERY", payload: query });
    }
  };

  useEffect(() => loadData());

  const { rows, skip, totalCount, loading } = state;
  return (
    <Paper>
      <Grid rows={rows} columns={columns} getRowId={getRowId}>
      <SelectionState
          selection={selection}
          onSelectionChange={setSelection}
        />
        <DateTypeProvider for={dateColumns} />
        <VirtualTableState
          infiniteScrolling
          loading={loading}
          totalRowCount={totalCount}
          pageSize={VIRTUAL_PAGE_SIZE}
          skip={skip}
          getRows={getRemoteRows}
        />
        <VirtualTable columnExtensions={tableColumnExtensions} />
        <TableHeaderRow />
      </Grid>
    </Paper>
  );
}
