import React, { useReducer, useEffect, MouseEventHandler } from "react";
import clsx from 'clsx';
import { createStyles, lighten, makeStyles, Theme } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';

import axios from "axios";
import reducer from "../../actions/calculatorReducer";

import {
  deleteCalculationById,
} from "../../actions/calculatorApiActions";

const URL = "https://localhost:44306";

const { forwardRef, useImperativeHandle } = React;


const buildQueryString = (skip: any, take: any) =>
  `${URL}/GetCalcList`;

const initialState = {
  rows: [],
  loading: false
};

interface CalculationListProps {
  handleCalculationEdit: (event: React.MouseEvent<unknown>, row: ICalculation) => void
}

const CalculationList = forwardRef((props: CalculationListProps, ref) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => loadData());
  const { rows } = state;

  useImperativeHandle(ref, () => ({
    updateRows(updatedRows: ICalculation[]) {
      dispatch({
        type: "UPDATE_ROWS",
        payload: {
          rows: updatedRows
        },
      });
    }
  }));

  const loadData = () => {
    const { requestedSkip, take, lastQuery, loading } = state;
    const query = buildQueryString(requestedSkip, take);
    if (query !== lastQuery && !loading) {
      dispatch({ type: "FETCH_INIT" });
      axios
        .get(query)
        .then((response) => {
          dispatch({
            type: "UPDATE_ROWS",
            payload: {
              rows: response.data,//.promotions,
            },
          });
        })
        .catch(() => dispatch({ type: "REQUEST_ERROR" }));
      dispatch({ type: "UPDATE_QUERY", payload: query });
    }
  }

  //}));

  function stableSort<ICalculation>(array: ICalculation[]) {
    const stabilizedThis = array.map((el: ICalculation, index) => [el, index] as [ICalculation, number]);
    return stabilizedThis.map((el) => el[0]);
  }

  interface EnhancedTableProps {
    numSelected: number;
    onSelectAllClick: (event: React.ChangeEvent<HTMLInputElement>) => void;
    rowCount: number;
  }

  const EnhancedTableHead = (props: EnhancedTableProps) => {
    const { onSelectAllClick, numSelected, rowCount } = props;

    return (
      <TableHead>
        <TableRow>
          <TableCell padding="checkbox">
            <Checkbox
              indeterminate={numSelected > 0 && numSelected < rowCount}
              checked={rowCount > 0 && numSelected === rowCount}
              onChange={onSelectAllClick}
              inputProps={{ 'aria-label': 'select all desserts' }}
            />
          </TableCell>

          <TableCell
            align={'left'}
            padding={'default'}
            sortDirection={false}
          >

          </TableCell>

        </TableRow>
      </TableHead>
    );
  }

  const useToolbarStyles = makeStyles((theme: Theme) =>
    createStyles({
      root: {
        paddingLeft: theme.spacing(2),
        paddingRight: theme.spacing(1),
      },
      highlight:
        theme.palette.type === 'light'
          ? {
            color: theme.palette.secondary.main,
            backgroundColor: lighten(theme.palette.secondary.light, 0.85),
          }
          : {
            color: theme.palette.text.primary,
            backgroundColor: theme.palette.secondary.dark,
          },
      title: {
        position: 'absolute',
        left: '0',
        width: '100%'
      },
      selectTitle: {
        flex: '1 1 16%',
        textAlign: 'left'
      }
    }),
  );

  interface EnhancedTableToolbarProps {
    numSelected: number;
    onDeleteClick: MouseEventHandler<unknown>;
  }

  const EnhancedTableToolbar = (props: EnhancedTableToolbarProps) => {
    const classes = useToolbarStyles();
    const { numSelected, onDeleteClick } = props;

    return (
      <Toolbar
        className={clsx(classes.root, {
          [classes.highlight]: numSelected > 0,
        })}
      >
        <Typography className={classes.selectTitle} color="inherit" variant="subtitle1" component="div">
          {(numSelected > 0 ? `${numSelected} selected` : '')}
        </Typography>

        <Typography className={classes.title} variant="h6" id="tableTitle" component="div">
          Calculation History
        </Typography>


        <Tooltip title="Delete" >
          <span>
            <IconButton disabled={numSelected === 0} onClick={onDeleteClick} aria-label="delete" >
              <DeleteIcon />
            </IconButton>
            </span>
        </Tooltip>
      </Toolbar>
    );
  };

  const useStyles = makeStyles((theme: Theme) =>
    createStyles({
      root: {
        width: '40%',
        margin: 'auto'
      },
      paper: {
        width: '100%',
        marginBottom: theme.spacing(3),
        marginTop: theme.spacing(3),
      },
      table: {
        minWidth: 750,
      },
      visuallyHidden: {
        border: 0,
        clip: 'rect(0 0 0 0)',
        height: 1,
        margin: -1,
        overflow: 'hidden',
        padding: 0,
        position: 'absolute',
        top: 20,
        width: 1,
      },
    }),
  );

  const EnhancedTable = () => {
    const classes = useStyles();
    const [selected, setSelected] = React.useState<string[]>([]);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(5);


    const handleSelectAllClick = (event: React.ChangeEvent<HTMLInputElement>) => {
      if (event.target.checked) {
        const newSelecteds = rows.map((n: ICalculation) => n.id?.toString());
        setSelected(newSelecteds);
        return;
      }
      setSelected([]);
    };

    const handleClick = (event: React.MouseEvent<unknown>, id: string) => {
      const selectedIndex = selected.indexOf(id.toString());
      let newSelected: string[] = [];

      if (selectedIndex === -1) {
        newSelected = newSelected.concat(selected, id);
      } else if (selectedIndex === 0) {
        newSelected = newSelected.concat(selected.slice(1));
      } else if (selectedIndex === selected.length - 1) {
        newSelected = newSelected.concat(selected.slice(0, -1));
      } else if (selectedIndex > 0) {
        newSelected = newSelected.concat(
          selected.slice(0, selectedIndex),
          selected.slice(selectedIndex + 1),
        );
      }

      setSelected(newSelected);
    };

    const handleCalculationDelete = (event: React.MouseEvent<unknown>, ids: string[]) => {
      deleteCalculationById(ids)
        .then((updatedRows) => {
          setSelected([]);
          dispatch({
            type: "UPDATE_ROWS",
            payload: {
              rows: updatedRows,
            },
          });
        })
        .catch(() => dispatch({ type: "REQUEST_ERROR" }));
    };

    const handleChangePage = (event: unknown, newPage: number) => {
      setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
      setRowsPerPage(parseInt(event.target.value, 10));
      setPage(0);
    };

    const isSelected = (id: string) => selected.indexOf(id) !== -1;

    const emptyRows = rowsPerPage - Math.min(rowsPerPage, rows.length - page * rowsPerPage);
    const getOperString = (op: oper) => {
      switch (op) {
        case 1://oper.add:
          return '+';
        case 2://oper.sub:
          return '-';
        case 3://oper.mul:
          return '*';
        case 4://oper.div:
          return ':';
      }
    }
    return (
      <div className={classes.root}>
        <Paper className={classes.paper}>
          <EnhancedTableToolbar
            numSelected={selected.length}
            onDeleteClick={(event) => handleCalculationDelete(event, selected)}
          />
          <TableContainer>
            <Table
              className={classes.table}
              aria-labelledby="tableTitle"
              size="medium"
              aria-label="enhanced table"
            >
              <EnhancedTableHead
                numSelected={selected.length}
                onSelectAllClick={handleSelectAllClick}
                rowCount={rows.length}
              />
              <TableBody>
                {stableSort(rows)
                  .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                  .map((row, index) => {
                    const isItemSelected = isSelected((row as any).id?.toString() || '');
                    const labelId = `enhanced-table-checkbox-${(row as any).id}`;
                    const actionDeleteId = `enhanced-table-action-delete-${(row as any).id}`;
                    const actionEditId = `enhanced-table-action-edit-${(row as any).id}`;
                    return (
                      <TableRow
                        hover
                        onClick={(event) => handleClick(event, (row as any).id?.toString() || '')}
                        role="checkbox"
                        aria-checked={isItemSelected}
                        tabIndex={-1}
                        key={(row as any).id}
                        selected={isItemSelected}
                      >
                        <TableCell padding="checkbox">
                          <Checkbox
                            checked={isItemSelected}
                            inputProps={{ 'aria-labelledby': labelId }}
                          />
                        </TableCell>
                        <TableCell component="th" id={labelId} scope="row" padding="none">
                          {(row as any).num1} {getOperString((row as any).oper)} {(row as any).num2} = {(row as any).result}
                        </TableCell>
                        <TableCell component="th" id={actionDeleteId} scope="row" padding="none">
                          <Tooltip title="Delete" >
                            <div onClick={(event) => handleCalculationDelete(event, [(row as any).id?.toString() || ''])}>
                              <IconButton aria-label="delete" >
                                <DeleteIcon />
                              </IconButton>
                            </div>
                          </Tooltip>
                        </TableCell>
                        <TableCell component="th" id={actionEditId} scope="row" padding="none">
                          <Tooltip title="Edit" >
                            <div onClick={(event) => props.handleCalculationEdit(event, row as ICalculation)}>
                              <IconButton aria-label="edit" >
                                <EditIcon />
                              </IconButton>
                            </div>
                          </Tooltip>
                        </TableCell>
                      </TableRow>
                    );
                  })}
                {emptyRows > 0 && (
                  <TableRow style={{ height: 53 * emptyRows }}>
                    <TableCell colSpan={6} />
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
          <TablePagination
            rowsPerPageOptions={[5, 10, 25]}
            component="div"
            count={rows.length}
            rowsPerPage={rowsPerPage}
            page={page}
            onChangePage={handleChangePage}
            onChangeRowsPerPage={handleChangeRowsPerPage}
          />
        </Paper>
      </div>
    );
  }

  return (<EnhancedTable></EnhancedTable>);
})

export default CalculationList;
