import React, { useState, useReducer, useEffect } from "react";
import Paper from "@material-ui/core/Paper";
import { VirtualTableState } from "@devexpress/dx-react-grid";
import {
  Grid,
  VirtualTable,
  TableHeaderRow,
} from "@devexpress/dx-react-grid-material-ui";

const VIRTUAL_PAGE_SIZE = 100;
const MAX_ROWS = 50000;
const URL =
  "https://js.devexpress.com/Demos/WidgetsGalleryDataService/api/Sales";
const getRowId = (row: { Id: any }) => row.Id;
const buildQueryString = (skip: any, take: any) =>
  `${URL}?skip=${skip}&take=${take}`;

const initialState = {
  rows: [],
  skip: 0,
  requestedSkip: 0,
  take: VIRTUAL_PAGE_SIZE * 2,
  totalCount: 0,
  loading: false,
  lastQuery: "",
};

function reducer(state: any, { type, payload }: any) {
  switch (type) {
    case "UPDATE_ROWS":
      return {
        ...state,
        ...payload,
        loading: false,
      };
    case "START_LOADING":
      return {
        ...state,
        requestedSkip: payload.requestedSkip,
        take: payload.take,
      };
    case "REQUEST_ERROR":
      return {
        ...state,
        loading: false,
      };
    case "FETCH_INIT":
      return {
        ...state,
        loading: true,
      };
    case "UPDATE_QUERY":
      return {
        ...state,
        lastQuery: payload,
      };
    default:
      return state;
  }
}

export default function PromotionTable() {
  const [state, dispatch] = useReducer(reducer, initialState);
  const columns = [
    {name: 'name', title: 'Name', getCellValue: (row: { name: any; }) => row.name},
    {name: 'type', title: 'Type', getCellValue: (row: { type: any; }) => row.type},
    {name: 'startDate', title: 'Start Date', getCellValue: (row: { startDate: any; }) => row.startDate},
    {name: 'endDate', title: 'End Date', getCellValue: (row: { endDate: any; }) => row.endDate},
    {name: 'userGroupName', title: 'User Group Name', getCellValue: (row: { userGroupName: any; }) => row.userGroupName},
    {name: 'Action', title: 'Action'},
];
  const [tableColumnExtensions] = useState([
    { columnName: "Id", width: 80 },
    { columnName: "ProductCategoryName", width: 220 },
    { columnName: "StoreName", width: 220 },
    { columnName: "SalesAmount", width: 120 },
  ]);

  const getRemoteRows = (requestedSkip: any, take: any) => {
    dispatch({ type: "START_LOADING", payload: { requestedSkip, take } });
  };

  const loadData = () => {
    const { requestedSkip, take, lastQuery, loading } = state;
    const query = buildQueryString(requestedSkip, take);
    if (query !== lastQuery && !loading) {
      dispatch({ type: "FETCH_INIT" });
      fetch(query)
        .then((response) => response.json())
        .then(({ data }) => {
          dispatch({
            type: "UPDATE_ROWS",
            payload: {
              skip: requestedSkip,
              rows: data,
              totalCount: MAX_ROWS,
            },
          });
        })
        .catch(() => dispatch({ type: "REQUEST_ERROR" }));
      dispatch({ type: "UPDATE_QUERY", payload: query });
    }
  };

  useEffect(() => loadData());

  const { rows, skip, totalCount, loading } = state;
  return (
    <Paper>
      <Grid rows={rows} columns={columns} getRowId={getRowId}>
        <VirtualTableState
          infiniteScrolling
          loading={loading}
          totalRowCount={totalCount}
          pageSize={VIRTUAL_PAGE_SIZE}
          skip={skip}
          getRows={getRemoteRows}
        />
        <VirtualTable columnExtensions={tableColumnExtensions} />
        <TableHeaderRow />
      </Grid>
    </Paper>
  );
}
