import React from "react";
import "./App.css";
import Calculator from "./components/Calculator/Calculator";
import CalculationList from "./components/calculationList/calculationList";
import { Header } from "./components/header/header";

var editCalculation: ICalculation;

function App() {
  const calculationList = React.createRef<typeof CalculationList>();
  const updateCalcList = () => {
    calculationList.current?.bind("loadData");
  };
  return (
    <div className="App">
      <Header />
      <Calculator editCalculation={editCalculation}
        onCalculationSave={updateCalcList} />
      <CalculationList ref={calculationList}
        handleCalculationEdit={(event: ICalculation) => editCalculation = event}
      />
    </div>
  );
}

export default App;
