import React, { useReducer } from "react";
import { FormControl, InputLabel, Select, MenuItem } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";

import reducer from "../../actions/calculatorReducer";

import {
    saveCalculation,
    deleteCalculationById,
    updateCalculation,
    getCalculationById,
} from "../../actions/calculatorApiActions";

export interface CalculatorProps {
    editCalculation: ICalculation,
    onCalculationSave: any
}

export interface CalculatorState { }

const Calculator = (props: CalculatorProps) => {
    //const initialState = {
    // calculation: {} as ICalculation
    //}
    const { editCalculation } = props;
    const [newCalculation, setState] = React.useState({
        ...editCalculation,
    });
    // const [state, dispatch] = useReducer(reducer, initialState);

    const handleChange = (event: any) => {
        const { name, value } = event.target;
        setState((prevState: any) => {
            return {
                ...prevState,
                [name]: value,
            };
        });
    };

    const handleSaveCalculation = (event: any) => {
        saveCalculation(newCalculation)
            .then((response) => {
                newCalculation.result = parseInt(response.data.toString());
                props.onCalculationSave();
            });
    }

    return (
        <form className="updatePromotionForm" noValidate autoComplete="off">
            <TextField
                id="num1"
                name="num1"
                defaultValue={newCalculation.num1}
                onChange={handleChange}
            />
            <FormControl style={{ width: "140px" }}>
                <Select
                    id="demo-simple-select"
                    name="oper"
                    value={newCalculation.oper}
                    onChange={handleChange}
                >
                    <MenuItem value={1}>+</MenuItem>
                    <MenuItem value={2}>-</MenuItem>
                    <MenuItem value={3}>*</MenuItem>
                    <MenuItem value={4}>:</MenuItem>
                </Select>
            </FormControl>
            <TextField
                id="num2"
                name="num2"
                defaultValue={newCalculation.num2}
                onChange={handleChange}
            />
            <Button
                onClick={() => handleSaveCalculation(newCalculation)}
                color="primary"
                autoFocus
            >=</Button>
            <InputLabel id="result" defaultValue={newCalculation.result} />
        </form>
    );
}
export default Calculator;
