import React from "react";
import "./App.css";
import Calculator from "./components/Calculator/Calculator";
import CalculationList from "./components/calculationList/calculationList";
import { Header } from "./components/header/header";

const { useRef } = React;

var editCalculation: ICalculation;

function App() {
  const childRef = useRef();

  const updateCalcList = (event: React.MouseEvent<unknown>, updatedRows: ICalculation[]) => {
    (childRef.current as any).updateRows(updatedRows);
  }

  return (
    <div className="App">
      <Header />
      <Calculator 
      editCalculation={editCalculation || {}}
        onCalculationSave={updateCalcList} />
      <CalculationList ref={childRef}
        handleCalculationEdit={(event: React.MouseEvent<unknown>, row: ICalculation) => editCalculation = row}
      />
    </div> 
  ); 
}

export default App;
