import React, { useState, useReducer, useEffect } from "react";
import Paper from "@material-ui/core/Paper";
import {
  Column,
  DataTypeProvider,
  DataTypeProviderProps,
  RowDetailState,
  SelectionState,
  VirtualTableState,
} from "@devexpress/dx-react-grid";
import {
  Grid,
  VirtualTable,
  TableHeaderRow,
  TableSelection,
  TableEditRow,
} from "@devexpress/dx-react-grid-material-ui";
import axios, { AxiosResponse } from "axios";
import reducer from "../actions/promotionReducer";
import {
  columnExtensions,
  promotionsTableColumns,
} from "../common/columnSchema";
import { MenuItem, Select } from "@material-ui/core";
import {
  addPromotion,
  deletePromotionById,
  getPromotionById,
} from "../actions/promotionApiActions";
import * as _ from "lodash";
const VIRTUAL_PAGE_SIZE = 10;
const MAX_ROWS = 10000;
const URL = "http://localhost:4000";
const getRowId = (row: any) => row._id;
const buildQueryString = (skip: any, take: any) =>
  `${URL}/getAllPromotions?skip=${skip}&take=${take}`;

const initialState = {
  rows: [],
  skip: 0,
  requestedSkip: 0,
  take: VIRTUAL_PAGE_SIZE * 2,
  totalCount: 0,
  loading: false,
  lastQuery: "",
};

const DateFormatter = ({ value }: { value: any }) => value.split("T")[0];

const DateTypeProvider = (props: any) => (
  <DataTypeProvider formatterComponent={DateFormatter} {...props} />
);

export default function PromotionTable() {
  const [state, dispatch] = useReducer(reducer, initialState);
  const columns: Column[] = promotionsTableColumns;
  const [tableColumnExtensions] = useState(columnExtensions);
  const [selection, setSelection] = useState<any | null>([]);
  const [dateColumns] = useState(["promotionStartDate", "promotionEndDate"]);

  const getRemoteRows = (requestedSkip: any, take: any) => {
    dispatch({ type: "START_LOADING", payload: { requestedSkip, take } });
  };

  const handlePromotionEdit = (promotionId: string) => {
    let editPromotion = getPromotionById(promotionId).then((response) => {
      response
    });
    setSelection([]);
  };

  const handlePromotionDelete = (promotionId: string) => {
    deletePromotionById(promotionId)
      .then(() => {
        setSelection([]);
        const updatedRows = state.rows.filter(function (row: any) {
          return row._id !== promotionId;
        });
        dispatch({
          type: "UPDATE_ROWS",
          payload: {
            rows: updatedRows,
          },
        });
      })
      .catch(() => dispatch({ type: "REQUEST_ERROR" }));
  };

  const handlePromotionDuplicate = (promotionId: string) => {
    let duplicatePromotion = _.find(state.rows, { _id: promotionId });
    addPromotion(duplicatePromotion)
      .then((response) => {
        console.log(response.data.promotion);
        setSelection([]);
        // const updatedRows = state.rows.splice(0, 0, response.data.promotion);
        const updateRows = state.rows;
        updateRows.unshift(response.data.promotion);
        dispatch({
          type: "UPDATE_ROWS",
          payload: {
            rows: updateRows,
          },
        });
      })
      .catch(() => dispatch({ type: "REQUEST_ERROR" }));
  };

  const onActionChange = (event: any) => {};

  const SelectActionType = () => (
    <Select
      onChange={onActionChange}
      style={{ width: "100%" }}
      disabled={selection.length === 0}
    >
      <MenuItem value="edit" onClick={() => handlePromotionEdit(selection[0])}>
        Edit
      </MenuItem>
      <MenuItem
        value="delete"
        onClick={() => handlePromotionDelete(selection[0])}
      >
        Delete
      </MenuItem>
      <MenuItem
        value="duplicate"
        onClick={() => handlePromotionDuplicate(selection[0])}
      >
        Duplicate
      </MenuItem>
    </Select>
  );

  const SelectTypeProvider = (props: any) => {
    console.log("SelectTypeProvider", props);
    return (
      <DataTypeProvider
        disabled={selection.length === 0}
        formatterComponent={SelectActionType}
        {...props}
      />
    );
  };

  const loadData = () => {
    const { requestedSkip, take, lastQuery, loading } = state;
    const query = buildQueryString(requestedSkip, take);
    if (query !== lastQuery && !loading) {
      dispatch({ type: "FETCH_INIT" });
      axios
        .get(query)
        .then((response) => {
          console.log(response.data.promotions);
          dispatch({
            type: "UPDATE_ROWS",
            payload: {
              skip: requestedSkip,
              rows: response.data.promotions,
              totalCount: MAX_ROWS,
            },
          });
        })
        .catch(() => dispatch({ type: "REQUEST_ERROR" }));
      dispatch({ type: "UPDATE_QUERY", payload: query });
    }
  };

  useEffect(() => loadData());
  const { rows, skip, totalCount, loading } = state;

  return (
    <Paper>
      <Grid rows={rows} columns={columns} getRowId={getRowId}>
        <SelectionState
          selection={selection}
          onSelectionChange={setSelection}
        />
        <SelectTypeProvider
          for={["Action"]}
          rowComponent
          row={RowDetailState}
        />

        <DateTypeProvider for={dateColumns} />

        <VirtualTableState
          infiniteScrolling
          loading={loading}
          totalRowCount={totalCount}
          pageSize={VIRTUAL_PAGE_SIZE}
          skip={skip}
          getRows={getRemoteRows}
        />
        <VirtualTable columnExtensions={tableColumnExtensions} />
        <TableSelection highlightRow={true} />
        <TableHeaderRow />
      </Grid>
    </Paper>
  );
}
